class BookList extends Component {
  render() {
    const allBooks = props.books;
    return (
      <Row xs={12} className="mt-5">
        {allBooks.map((book) => (
          <SingleBook book={book} />
        ))}
      </Row>
    );
  }
}

export default BookList;
